<?phpinclude_once('inc/C_Page.php');include_once('inc/C_Catalog.php');//site.ru/index.php?c=user&act=index$action = 'action_';$action .= (isset($_GET['act'])) ? $_GET['act'] : 'index';// switch ($_GET['c']){	case 'page':		$controller = new C_Page();    case 'goods':		$controller = new C_Catalog();		case 'user':            $controller = new C_User();		break;	default:		$controller = new C_Page();}$controller->$action();$controller->render();