<?php//// ����������� �������� ������.//include_once('inc/model.php');class C_Page extends C_Base{	//	// �����������.	//	function __construct(){				parent::__construct();	}		public function action_index(){    $this->title .= '::������';    $text = text_get();    $this->content = $this->Template('theme/v_index.php', array('text' => $text));    }    public function action_test(){        $this->title .= 'Партнеры';        $obj = new M_Partners();        $partners = $obj->getPartners();        $this->content = $this->Template('theme/v_partners.php', array('p' => $partners));    }    public function action_partners(){        $this->title .= 'Партнеры';        $obj = new M_Partners();        $partners = $obj->getPartners();        $this->content = $this->Template('theme/v_partners.php', array('p' => $partners));    }		public function action_edit(){		$this->title .= '::��������������';				if($this->isPost())		{			text_set($_POST['text']);			header('location: index.php');			exit();		}				$text = text_get();		$this->content = $this->Template('theme/v_edit.php', array('text' => $text));			}}