<?phpinclude_once('m/model.php');class C_page extends C_base{	public function action_index(){		$this->title .= '::Каталог';        $goods = new Goods();        $goods_arr = $goods->getCatalog();		$this->content = $this->Template('v/v_index.php', array('items' => $goods_arr));        if($this->isPost()) {           erer:ere();        }	}    public function action_item() {        $goods = new Goods();        $good = $goods->getGood($_GET['id']);        $this->title .= '::'.$good['title'];        $this->content = $this->Template('v/goods_item.php', array('item' => $good));        // добавляем количество просмотров к товару и к юзеру        $goods->addView($_GET['id']);    }    public function action_photo() {        $goods = new Goods();        $good = $goods->getGood($_GET['id']);        $this->title .= '::'.$good['title'];        $this->content = $this->Template('v/photo.php', array('text' => $good['photo']));    }}