<?phprequire_once 'config/db.php';class Goods {    public $id, $title, $param, $price, $hit, $views, $discount, $photo;    public function getCatalog () {        $connect = new DB;        return $connect->Select('goods', false, false, true);    }    public function getGood ($id) {        $connect = new DB;        return $connect->Select('goods', 'id', $id, false);    }    public function addView($id) {        $connect = new DB;        $connect->Insert('views_log', array(            'user_id'=>$_SESSION['user_id'],                'good_id'=>$id,            'view_date'=>date("Y-m-d H:i:s"))        );        $good = self::getGood($id);        $views = $good['views']+1;        $connect->Update('goods', array('views'=>$views), 'id='.$id);    }}?>