<?phpabstract class C_Base extends C_Controller {	protected $title;	protected $content;	protected function before() {		$this->title = '';		$this->content = '';		$cart = M_Cart::createUserCart();		$cart_count = M_Cart::getCartCount();		$this->cart_count = $cart_count;		$conditions = M_User::authStatus();		$status = $conditions['status'];		$this->status = $status;		$isAuth = $conditions['isAuth'];		$this->isAuth = $isAuth;		$user = $_SESSION['user_name'];		$this->user = $user ? $user : 'Гость';		if (!M_User::isUserAuth()) {			M_User::createGuest();		}		$isProductAdd = M_Cart::isProductAdd();		if ($isProductAdd) {			M_Cart::addProduct();		}	}	public function render() {		$vars = array(			'title' => $this->title,			'content' => $this->content,			'user' => $this->user,			'cart_count' => $this->cart_count,			'isAuth' => $this->isAuth,		);		$page = $this->Template('v_main.php', $vars);		echo $page;	}}