<?phpabstract class C_Controller {	protected abstract function render();	protected abstract function before();	public function Request($action) {		$this->before();		$this->$action(); //$this->action_index		$this->render();	}	protected function IsGet() {		return $_SERVER['REQUEST_METHOD'] == 'GET';	}	protected function IsPost() {		return $_SERVER['REQUEST_METHOD'] == 'POST';	}	protected function Template($fileName, $vars = array()) {		$loader = new Twig_Loader_Filesystem('v');		$twig = new Twig_Environment($loader);		$template = $twig->loadTemplate($fileName);		return $template->render($vars);	}	public function __call($name, $params) {		die('вводите правильные данные в строку url !!!');	}}