<?phpclass C_User extends C_Base {	public function action_auth() {		$this->title .= 'Авторизация';		$conditions = M_User::authStatus();		$status = $conditions['status'];		$_SESSION['isAuth'] = $conditions['isAuth'];		$user = $conditions['user'];		$this->content = $this->Template('v_auth.php', array('status' => $status, 'user' => $user));	}	public function action_register() {		$this->title .= 'Регистрация';		$passMatch = M_User::registerStatus();		$this->content = $this->Template('v_register.php', array('passMatch' => $passMatch));	}	public function action_registered() {		$this->title .= 'Регистрация';		$this->content = $this->Template('v_registered.php');	}	public function action_cabinet() {		$this->title .= 'Личный кабинет';		$this->user_id = $_SESSION['user_id'];		//парсим лог файл действий пользователей		$lastActions = M_UserNavigation::getUserNav($this->user_id);		//определяем последние 5 действий текущего пользователя		$this->lastActions = array_slice($lastActions, -5);		$this->content = $this->Template('v_cabinet.php', array('user' => $this->user, 'lastActions' => $this->lastActions));	}	public function action_destroy() {		session_start();		session_destroy();		header('location: index.php');		exit();	}}