<?phprequire_once 'autoload.php';session_start();$action = 'action_';$action .= (isset($_GET['act'])) ? $_GET['act'] : 'index';switch ($_GET['c']) {case 'page':	$controller = new C_Page();	break;case 'user':	$controller = new C_User();	break;case 'cart':	$controller = new C_Cart();	break;default:	$controller = new C_Page();	break;}$controller->Request($action);