<?phpclass M_Page extends M_Base {	//создаём массив изображений из БД	public function createImagesArr() {		$startValue = 1;		$startLimit = $startValue + 3;		return M_DB::getAssocResult("SELECT	    img_id,	    `name`,	    concat(img_link.folder, img_link.path) AS full_path,	    concat(compr_img_link.folder, compr_img_link.path) AS compr_full_path,	    width,	    height,	    view_count,	    price,	    good_name,	    value AS discount,	    round(price * (100 - value)/100) AS discounted_price	    FROM images	    INNER JOIN img_link ON images.link_id = img_link.link_id	    INNER JOIN compr_img_link ON images.compr_link_id = compr_img_link.link_id	    INNER JOIN discount ON images.discount_id = discount.id	    WHERE img_id BETWEEN :start AND :limit;",			$startValue,			$startLimit		);	}	public function productGet() {		$images_full = self::createImagesArr();		$productId = (int) $_GET['id'];		$image = NULL;		foreach ($images_full as $img) {			if ($img['img_id'] == $productId) {				$image = $img;			}		}		//увеличиваем количество просмотров данного продукта		self::increaseCounter($image['img_id']);		return $image;	}	public function getSelectedProduct() {		//$selected_product		return $_COOKIE['product'];	}	//функция увеличивает счетчик просмотра картинки на 1	private function increaseCounter(int $id) {		M_DB::getCart('UPDATE `galery`.`images`	    SET `view_count` = `view_count` + 1 WHERE (`img_id` = :id);'			, $id);	}}