<div class="content">  {% if imagesArr|length > 0 %}  <ul class="content_list">  {% for image in imagesArr %}    <li>      <a class="link" href="/index.php?c=page&act=product&id={{image.img_id}}">        <span class="img_views">          просмотров: {{image.view_count}}        </span>        <img          class="product_img"          id={{image.img_id}}          src={{image.compr_full_path}}          width={{image.width}}          height={{image.height}}          alt={{image.name}}        >        <span class="good_name">          {{image.good_name}}        </span>        <span class="img_price">          Цена: {{image.price}} USD        </span>        <form class="buy-btn-form" method="post">          <button type="submit" name="buy" class="btn btn-success buy-btn" value="{{image.img_id}}">В коризну</button>        </form>      </a>    </li>  {% endfor %}  </ul>  <div class="more">    <button class="more_btn">ЕЩЁ</button>  </div>  {% endif %}  {% if product %}  <div class="modal_div">    <img class="modal_img" src={{product.full_path}} alt={{product.name}}>    <span class="img_views">      Количество просмотров: {{product.view_count + 1}}    </span>    <a href="index.php?page=1">      <button class="modal_exit">X</button>    </a>  </div>  {% endif %}</div>